function updateTextArea() {
   var allVals = [];
   $('.taglist :checked').each(function(i) {

       allVals.push((i!=0?"\r\n":"")+ $(this).val());
   });
   $('#video0_tags').val(allVals).attr('rows',allVals.length) ;

   }
   $(function() {
      $('.taglist input').click(updateTextArea);
	  
      updateTextArea();
});


